	[Description] [text] NOT NULL,
	CONSTRAINT [PK_RightsDetail] PRIMARY KEY ([Id])
)
GO
-- TABLE: RIGHTS DETAIL - END

-- TABLE: ROLE RIGHTS
CREATE TABLE [RoleRights](
	[Id] [int] IDENTITY(1,1) NOT NULL,
	[RoleId] [int] NOT NULL,
