	[MemoryModules] [int] NULL,
	[PSUFormFactor] [varchar](100) NULL,
	[ScreenSize] [float] NULL,
	[Resolution] [varchar](50) NULL,
	[Description] [text] NULL,
	[Other] [text] NULL,
