-- TABLE: PREBUILT
CREATE TABLE [PreBuilt](
	[Id] [int] IDENTITY(1,1) NOT NULL,
	[Name] [nvarchar](100) NOT NULL,
	[Detail] [text] NOT NULL,
	[CaseId] [int] NULL,
	[MonitorId] [int] NULL,
	[PSUId] [int] NULL,
	[StorageId] [int] NULL,
	[VGAId] [int] NULL,
	[MemoryId] [int] NULL,
	[CPUCoolerId] [int] NULL,
	[MotherBoardId] [int] NULL,
	[CPUId] [int] NULL,
	Price [float] NULL,
	Stock [int] NULL,
	[CustomerId] [int] NULL,
